// ==UserScript==
// @name         Search on KBB
// @namespace    http://tampermonkey.net/
// @version      2024-09-08
// @description  Adds a "Search on KBB' button on TrueCar website to check "Fair Price" mentioned on the KellyBlueBook Website
// @author       jvalant52
// @match        https://www.truecar.com/*
// @icon         https://www.google.com/s2/favicons?sz=64&domain=truecar.com
// @grant        none
// ==/UserScript==

(function() {

    function tryInsertButton(retries = 5) {
        // Try to find the searchKBB element
        const searchElement = document.querySelector(".flex-col-reverse > div:nth-child(1) > h1:nth-child(1) > div:nth-child(1)");

        // Check if element is found and has content
        if (searchElement && searchElement.innerHTML.trim()) {
            const searchKBB = searchElement.innerHTML;

            // Step 1: Modify the search term
            const words = searchKBB.split(" ");
            words.length = 3;

            // Step 2: Construct the KBB URL
            let kBBURL = `https://www.kbb.com/${(words[1])}/${(words[2])}/${(words[0])}`;

            // Step 3: Create a new <button> element for the link styled as a button
            const newButton = document.createElement('button');
            newButton.style.display = "inline-flex";
            newButton.style.alignItems = "center";
            newButton.style.fontSize = '0.875rem';
            newButton.style.backgroundColor = "#007bff"; // Blue background
            newButton.style.color = "white"; // White text
            newButton.style.border = "none";
            newButton.style.borderRadius = "8px"; // Rounded corners
            newButton.style.padding = "10px 15px"; // Padding
            newButton.style.marginLeft = "10px"; // Margin
            newButton.style.cursor = "pointer"; // Pointer cursor on hover

            // Step 4: Create the image element (PNG icon)
            const buttonImage = document.createElement('img');
            buttonImage.src = 'data:image/png;base64,'
            buttonImage.style.height = "20px";
            buttonImage.style.marginRight = "8px"; // Space between image and text

            // Step 5: Create a text node for the button
            const buttonText = document.createTextNode('Search on KBB');

            // Step 6: Append image and text to the button
            newButton.appendChild(buttonImage);
            newButton.appendChild(buttonText);

            // Add a click event to redirect to the KBB URL
            newButton.onclick = function() {
                window.open(kBBURL, '_blank');
            };

            // Step 7: Insert the button next to the specified <div>
            const targetDiv = document.querySelector('[data-test="marketplaceVdpHeader"]');
            if (targetDiv) {
                targetDiv.insertAdjacentElement('beforeend', newButton);
            } else {
                console.log("Target div not found.");
            }
        } else if (retries > 0) {
            console.log(`Element not found, retrying... (${retries} retries left)`);
            setTimeout(() => tryInsertButton(retries - 1), 3000); // Retry after 3 seconds
        } else {
            console.log("Failed to find the element after 10 seconds.");
        }
    }

    tryInsertButton(5); // Initial attempt
})();
